import numpy as np


def add_commas_to_matrix(matrix_string):
    matrix = []
    # Split the string into lines
    lines = matrix_string.strip().split('\n')
    # Add a comma at the end of each line except the last one
    for line in lines:
        row = [float(num) for num in line.strip('[]').split(',')]
        matrix.append(row)

    # Join the lines back into a single string with new lines
    return np.array(matrix)


